#define SCREEN_W 383
#define SCREEN_H 216
#define BLOCK_W 50
#define BLOCK_H 50
#define JUMP_POWER 4
#define GRAVITY 2
#define PILLAR_W 10
#define PILLAR_H 85

// Prototypes
int jump(int *y, int *springmulti);
int gravity(int *y, int *valtijd);
void draw(int *x, int *y, int *px, int *py);

int main(void) {
    int x = 50;
    int y = 0;
    int px = 380;
    int py = 50;
    int last = 1;
	int valtijd = 0;
	int springtijd = 0;
	int springmulti = 4;
	int time = 0;

    while (1) {
        CMT_Delay_100micros(100);

		Bdisp_AllClr_VRAM();
		draw(&x, &y, &px, &py);
		Bdisp_PutDisp_DD();

		time++;
		if (time %= 5) px -= 6;
		if (px <= 0 + PILLAR_W) {
			px = 382;
			py = rand() % (SCREEN_H - PILLAR_H);
		}

		if (springtijd == 0) {
			valtijd++;
        	gravity(&y, &valtijd);
		}
		if (y + BLOCK_H >= SCREEN_H) y = SCREEN_H - BLOCK_H;
		if (springtijd <= 3 && springtijd >= 1) {
			jump(&y, &springmulti);
			springtijd++;
			springmulti--;
		}
		else {
			springtijd = 0;
			springmulti = 4;
		}
        if (PRGM_GetKey() == 28 && last == 1) {
			springtijd++;
            jump(&y, &springmulti);
            last = 0;
			valtijd = 0;
        }
        else if (PRGM_GetKey() != 28) last = 1;
        if (PRGM_GetKey() == 47) break;
		if (px + PILLAR_W <= x + BLOCK_W && px >= x && py + PILLAR_H >= y && py <= y + BLOCK_H) break;


    }

    return 0;
}

int gravity(int *y, int *valtijd) {
    if (*y + BLOCK_H <= SCREEN_H) *y += GRAVITY * *valtijd;
    else *y = SCREEN_H - BLOCK_H + 1;
    return *y;
}
void draw(int *x, int *y, int *px, int *py) {
	for (int j = 0; j <= BLOCK_H; j++) {
		for (int i = 0; i <= BLOCK_W; i++) {
			Bdisp_SetPoint_VRAM(*x + i, *y + j, sprite[j*BLOCK_H + i]);
		}
	}
	for (int j = 0; j <= PILLAR_H; j++) {
		for (int i = 0; i <= PILLAR_W; i++) {
			Bdisp_SetPoint_VRAM(*px + i, *py + j, 0xf700);
		}
	}
}

int jump(int *y, int *springmulti) {
    *y -= JUMP_POWER * *springmulti;
    return *y;
}